INSERT INTO `tb_dataset` (`id_dataset`, `id_user`, `user_account`, `tweet`, `clean_tweet`, `sentimen`) VALUES
(45569, 1, '@RssfForever', 'Omnibus Law Cipta Kerja merupakan RUU usulan Jokowi dan masuk sebagai RUU Prioritas Tahun 2020 dalam Program Legislasi Nasional Tahun 2020. #OBLBuatRakyatSejahtera3 Manfaat UU Cipta Kerja untuk Rakyat Seperti yang Diklaim Jokowi Halaman all - Kompas.com\nPresiden Joko Widodo (Jokowi) membeberkan 3 manfaat besar dari disahkannya RUU Omnibus Law Cipta Kerja menjadi UU Cipta Kerja. Halaman all\nmoney.kompas.com', 'omnibus law cipta kerja rancang undang undang usul jokowi masuk rancang undang undang prioritas tahi program legislasi nasional tahi manfaat undang undang cipta kerja rakyat klaim jokowi halaman all presiden joko widodo jokowi beber manfaat sah rancang undang undang omnibus law cipta kerja undang undang cipta kerja halaman all money', '1'),
(45570, 1, '@Lady_Medussa', 'Tolong ada yg masih nyimpen trit ttg RUU Cipta kerja kemaren ga?', 'tolong simpan utas rancang undang undang cipta kerja kemarin', '1'),
(45571, 1, '@Baretqu', 'Setelah RUU Cipta Kerja disetujui menjadi undang-undang dalam rapat paripurna DPR, Senin (5/10/2020), sejumlah hoax tersebar di masyarakat.\n\n#OmnibusLawBangunBangsa ', 'rancang undang undang cipta kerja tuju undang undang rapat paripurna dewan wakil rakyat senin hoax sebar masyarakat', '1'),
(45572, 1, '@Baayam1', 'Prabowo mengaku sejak awal telah menginstruksikan Fraksi Gerindra di Dewan Perwakilan Rakyat untuk meneliti RUU Cipta Kerja tiap klaster hingga pasalnya. #RakyatButuhUUCiptaKerja', 'prabowo aku instruksi fraksi gerindra dewan wakil rakyat teliti rancang undang undang cipta kerja kluster pasal', '1'),
(45573, 1, '@KantahCilegon', '#Repost \n@atr_bpn\n#SobATRBPN, kemudahan pengurusan lahan akan dibenahi melalui RUU Cipta Kerja, guna meningkatkan iklim investasi yang kemudian akan menciptakan lapangan kerja.\n.\n#UntukEkonomiIndonesia\n#IndonesiaMaju\n#RUUCiptaKerja', 'mudah urus lahan benah rancang undang undang cipta kerja tingkat iklim investasi cipta lapang kerja', '1'),
(45574, 1, '@superrsid', 'Membalas \n@CNNIndonesiaJadi yang Punya draft RUU/UU Cipta kerja yg disahkan, jika bukan dari link resmi bisa di tuduh menyebarkan hoax.. \n\nehh gimana sikk ', 'balas draf rancang undang undang undang undang cipta kerja sah link resmi tuduh sebar hoax sih', '1'),
(45575, 1, '@silviaA03016926', 'Dengan adanya Omnibus Law RUU Cipta kerja menjadikan Indonesia menjadi lebih baik', 'omnibus law rancang undang undang cipta kerja jadi indonesia', '1'),
(45576, 1, '@GueTionghoa', 'Buat yg merasa aktifitasnya di seputar perizinan mendapatkan kenyamanan dengan kesalahan jalur.. Maka akan sangat terusik dengan adanya RUU Cipta Kerja ini.. Karena akan merubah semua bentuk perzinan yg terkesan lelet. \n\n#OmnibusLawBasmiKorupsi', 'aktivitas putar izin nyaman salah jalur usik rancang undang undang cipta kerja rubah bentuk zan kes lelet', '1'),
(45577, 1, '@Dewisulastini1', 'Ketua DPR: Draft RUU Cipta Kerja Hanya Dicek Acak, World Bank Justru Memujinya? | Serba-serbi MMC\n\nhttps://youtu.be/DZmnBOZptuA\n\nJangan lupa share seluas-luasnya Ketua DPR: Draft RUU Cipta Kerja Hanya Dicek Acak, World Bank Justru...\nPengesahan UU Cipta Kerja sudah dua minggu berlalu. Sudahlah rapat larut malam saat weekend demi sahnya RUU Cilaka ini, ternyata eh ternyata pimpinan DPR men...\nyoutube.com', 'ketua dewan wakil rakyat draf rancang undang undang cipta kerja cek acak dunia bank puji serba serbi mmc', '0'),
(45578, 1, '@03__nakula', 'Tahapan berikut nya draft disampaikan ke presiden, lalu presiden adakan rapat terbatas RUU Omnibus Law Cipta kerja masuk tahap finalisasi 15 Januari 2020.\n\nKemudian DPR menetapkan RUU cipta kerja sebagian program legislasi nasional prioritas tahun 2020.\n\n#OmnibusLawTaatProseduralWong Agung dan 9 lainnya', 'tahap draf presiden presiden rapat batas rancang undang undang omnibus law cipta kerja masuk tahap final januari dewan wakil rakyat tetap rancang undang undang cipta kerja program legislasi nasional prioritas tahi agung', '1'),
(45579, 1, '@iqbalangr', 'Rapat-Rapat Penentu RUU Cipta KerjaRapat-Rapat Penentu RUU Cipta Kerja - Tirto.ID\nRapat-rapat Cipta Kerja di beberapa hotel menghasilkan beberapa keputusan, yang itu berubah lagi seketika di DPR RI.\ntirto.id', 'rapat rapat tentu rancang undang undang cipta kerja rapat rapat tentu rancang undang undang cipta kerja tirto rapat rapat cipta kerja hotel hasil putus ubah dewan wakil rakyat rakyat indonesia tirto', '1'),
(45580, 1, '@mitatarmila', 'maka terciptalah RUU Cipta Kerja ini. dengan adanya RUU ini diharapkan investor bisa berdatangan dan lapangan pekerjaan terbuka lebar.\n\nItu artinya pembentukan Perppu merupakan jalan yang irasional, mengingat RUU Cipta Kerja ini merupakan inisiatif dari pemerintah sendiri,', 'cipta rancang undang undang cipta kerja rancang undang undang harap investor lapang kerja buka lebar bentuk atur pusat jalan irasional rancang undang undang cipta kerja inisiatif perintah', '1'),
